// 示例1:

// 输入: s1 = "ab" s2 = "eidbaooo"
// 输出: True
// 解释: s2 包含 s1 的排列之一 ("ba").
//  

// 示例2:

// 输入: s1= "ab" s2 = "eidboaoo"
// 输出: False

// 来源：力扣（LeetCode）
// 链接：https://leetcode-cn.com/problems/permutation-in-string
// 著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
#include <iostream>
#include <string>

using namespace std;

class Solution {
 public:
  static bool checkInclusion(string s1, string s2) {
    int cnt = 0;
    int start_from = 0;
    string s1_back = s1;
    for (int i = 0; i < s2.length(); i++) {
      int pos = s1.find(s2.at(i));
      if (pos != std::string::npos) {
        cnt++;
        if (cnt == 1) start_from = i;
        s1.erase(pos, 1);
        // std::cout << s1.size() << std::endl;
      } else {
        if (cnt != 0) i = start_from;
        cnt = 0;
        s1 = s1_back;
      }
      if (s1.empty()) return true;
    }
    return false;
  }
};

int main() {
  // std::string s1 = "hello";
  // std::string s2 = "ooolleoooleh";
  
  // std::string s1 = "adc";
  // std::string s2 = "dcda";

  // std::string s1 = "ab";
  // std::string s2 = "eidbaooo";
  std::string s1 = "";
  std::string s2 = "";

  std::cout << "result: " << std::boolalpha << Solution::checkInclusion(s1, s2)
            << std::endl;
}